import { Component,OnInit } from '@angular/core';
import { Product } from '../product';
import { GetDataService } from '../get-data.service';

@Component({
  selector: 'app-catalogue',
  templateUrl: './catalogue.component.html',
  styleUrls: ['./catalogue.component.css']
})
export class CatalogueComponent implements OnInit{
  selectedProduct: Product | null = null;
  products : Product[]= []
  modalVisible = false;
  selectedPrice=""
  selectedName=""
  selectedCategory=""
  cartModalVisibile = false

  openModal(product: Product) {
    this.selectedProduct = product;
    this.modalVisible = true;

  }
  closeModal() {

    this.modalVisible = false;
    console.log(this.modalVisible)
 }

  sortByName(sortVal:string){

    let data = localStorage.getItem('data');
    let arr :Product[] = data ? [...JSON.parse(data)] : [];

    if(sortVal ==="asc"){
    arr.sort((a:Product,b:Product)=>{
      return a.title.localeCompare(b.title);
    })
    }else if(sortVal==="desc"){
      arr.sort((a:Product,b:Product)=>{
        return b.title.localeCompare(a.title);
      })
    }
    this.products = [...arr]
    this.selectedName = sortVal
  }

  sortByPrice(sortVal:string){
    let data = localStorage.getItem('data');
    let arr :Product[] = data ? [...JSON.parse(data)] : [];
    if(sortVal ==="asc"){
      arr.sort((a:Product,b:Product)=>{
        return a.price-b.price
      })
      }else if(sortVal==="desc"){
        arr.sort((a:Product,b:Product)=>{
          return b.price-a.price
        })
      }
      this.products = [...arr]
      this.selectedPrice = sortVal
  }
  filterByCategory(filterVal:string){
    let data = localStorage.getItem('data');
    let arr :Product[] = data ? [...JSON.parse(data)] : []
    if(filterVal==="men's clothing"){
      this.products = arr.filter((ele)=>ele.category==="men's clothing")
    }else if(filterVal==="jewellery"){
      this.products = arr.filter((ele)=>ele.category==="jewellery")
    }else if(filterVal==="electronics"){
      this.products = arr.filter((ele)=>ele.category==="electronics")
    }else if(filterVal==="women's clothing"){
      this.products = arr.filter((ele)=>ele.category==="women's clothing")
    }else{
      this.products = [...arr]
    }
    this.selectedCategory= filterVal

this.selectedCategory=filterVal
  }


  async ngOnInit(): Promise<void> {
    try {
      this.products = await this.getDataService.getProducts()
    } catch (error) {
      console.log(error)
    }

  }
  addToFav(id:number){
     this.products=this.products.map((ele)=>ele.id === id ? {...ele,fav:true}:ele)
    // 
    let data = localStorage.getItem('data');
    let arr :Product[] = data ? JSON.parse(data) : [];
    for (let i = 0; i < arr.length; i++) {
      if (arr[i].id === id) {
        arr[i].fav = true;
        break; 
      }
    }
    localStorage.setItem("data",JSON.stringify(arr))
  }
  addToCart(id:number){
    this.products = this.products.map((ele)=>ele.id === id? {...ele,cart:true}:ele)
    let data = localStorage.getItem('data');
    let arr :Product[] = data ? JSON.parse(data) : [];
    for (let i = 0; i < arr.length; i++) {
      if (arr[i].id === id) {
        arr[i].cart = true;
        break; 
      }
    }
    localStorage.setItem("data",JSON.stringify(arr))
  }
  removeFromCart(id:number){
    this.products = this.products.map((ele)=>ele.id === id? {...ele,cart:false}:ele)
    let data = localStorage.getItem('data');
    let arr :Product[] = data ? JSON.parse(data) : [];
    for (let i = 0; i < arr.length; i++) {
      if (arr[i].id === id) {
        arr[i].cart = false;
        break; 
      }
    }
    localStorage.setItem("data",JSON.stringify(arr))
  }
  removeFromFav(id:number){
   this.products= this.products.map((ele)=>ele.id === id ? {...ele,fav:false}:ele)
   let data = localStorage.getItem('data');
    let arr :Product[] = data ? JSON.parse(data) : [];
    for (let i = 0; i < arr.length; i++) {
      if (arr[i].id === id) {
        arr[i].fav = false;
        break; 
      }
    }
   localStorage.setItem("data",JSON.stringify(arr))
  }

  constructor(private getDataService: GetDataService){

  }
}
<div style="width: 100%; padding: 20px;">
    <select [ngModel] = "selectedName" (ngModelChange)="sortByName($event)" name="sortbyname">
        <option value="">Sort By Name</option>
        <option value="asc">Ascending</option>
        <option value="desc">Descending</option>
    </select>
    <select [ngModel]="selectedPrice" (ngModelChange)="sortByPrice($event)" name="sortbyprice">
        <option value="">Sort By Price</option>
        <option value="asc">Ascending</option>
        <option value="desc">Descending</option>
    </select>
    <select [ngModel]="selectedCategory" (ngModelChange)="filterByCategory($event)" name="filter">
        <option value="">Filter By Category</option>
        <option value="men's clothing">Men's clothing</option>
        <option value="women's clothing">Women's clothing</option>
        <option value="jewelery">Jewellery</option>
        <option value="electronics">Electronics</option>
    </select>
</div>
<div style="width: 100%; padding: 40px; display: grid; grid-template-columns: repeat(3,1fr); margin: auto; column-gap: 10px; row-gap: 10px; gap: 10px;">
<div style="margin-bottom: 10px; margin-right: 10px;" *ngFor="let product of products">
<div style="width: 100%;">
    <img style="width: 100px; height: 100px;" [src]="product.image"/>
<h1>Name: {{product.title}}</h1>
<div>
<button type="button" class="focus:outline-none text-white bg-purple-700 hover:bg-purple-800 focus:ring-4 focus:ring-purple-300 font-medium rounded-lg text-sm px-5 py-2.5 mb-2 dark:bg-purple-600 dark:hover:bg-purple-700 dark:focus:ring-purple-900" (click)="!product.fav? addToFav(product.id): removeFromFav(product.id)" >{{product.fav? "Remove from favourites":"Add to favourites"}}</button>
</div>
<div>
<button type="button" class="focus:outline-none text-white bg-purple-700 hover:bg-purple-800 focus:ring-4 focus:ring-purple-300 font-medium rounded-lg text-sm px-5 py-2.5 mb-2 dark:bg-purple-600 dark:hover:bg-purple-700 dark:focus:ring-purple-900" (click) = "!product.cart ? addToCart(product.id): removeFromCart(product.id)">{{product.cart?"Remove from cart":"Add to cart"}}</button>
</div>
<!-- <button [hidden]="product.fav" (click) = "removeFromFav(product.id)">Remove from Fav</button>
<button [hidden] ="!product.fav" (click) = "addToFav(product.id)">Add to Fav</button> -->


<!-- Modal toggle -->

<button  type="button" class="focus:outline-none text-white bg-purple-700 hover:bg-purple-800 focus:ring-4 focus:ring-purple-300 font-medium rounded-lg text-sm px-5 py-2.5 mb-2 dark:bg-purple-600 dark:hover:bg-purple-700 dark:focus:ring-purple-900" (click)="openModal(product)" id="myBtn">Open Modal</button>

<!-- The Modal -->
<div id="myModal" [hidden]="!modalVisible" class="modal" >

  <!-- Modal content -->
  <div class="modal-content">
    <button  (click)="closeModal()" class="close">Close</button>
    <img style="width: 100px; height: 100px;" [src]="selectedProduct?.image"/>
<h1>Name: {{selectedProduct?.title}}</h1>

    <p>Price: {{selectedProduct?.price}}</p>
    <p>Description: {{selectedProduct?.description}}</p>
    <button [hidden] = "selectedProduct?.fav">Add to Favourite</button>
  </div>

</div>


</div>
</div>
</div>
